 import React from 'react';
 import {Link} from 'react-router-dom';
 
{/* Ovo nam je slicno kao za Button gde smo definisali za sve samo je ovo za Cards (odnosno za slike koje ce se nalaziti na pocetnoj strani)*/}
{/* */}
function CardItem(props) {
   return (
     <>
     {/* Props bi trebalo da cuva prostora za ono sto cemo mi zapravo staviti
        npr img ima props src a taj src zapravo stavljamo u Card u drugom komponentu unutar CardItem componenta
        Stavimo props.name i posle negde stavimo nama= Aleksa i to se automatski smesta u props.name
        Stavili smo props.label i posle smo stavili da je u CardItem label = adventure u sto je i desilo*/}
        <li className='cards__item'>
            <Link className='cards__item__link' to={props.path}>
                <figure className='cards__item__pic-wrap' data-category={props.label}>
                    <img src={props.src} alt='Travel' className='cards__item__img'></img>
                </figure>
                <div className='cards__item__info'>
                    <h5 className='cards__item__text'>
                        {props.text}
                    </h5>
                </div>
            </Link>
        </li>
     </>
    );
}
 
export default CardItem;
