import React,{useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import { Button } from './Button';
import './Navbar.css';

{/* Ovo nam je NavBar i njegova logika*/} 
function Navbar() {
    {/*Ovde smo definisali click tu je useState false tako da ce biti linice*/}
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true)
    {/* Ovo je funkcija koja ce da koristi definisani click a ona ce da bude suprotno od onoga sto je u useState, znaci ako je tamo false kada se klikne bice true
        i ispunice se uslov dole, znaci nama je postavljeno zbog useState da budu licice a kada se klikne bice suprotno od linica, zanci X false and true */}
    const handleClick = () => setClick(!click);
    {/* closeMobileMenu nam je funkcija koja ce da nam zatvara meni i postvlja da nam dugme za meni bude linice jer je setClick postavljen na false*/}
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if(window.innerWidth <= 960) {
            setButton(false);
        }else{
            setButton(true);
        }
    };

    useEffect(() => {
        showButton();
    }, []);

    window.addEventListener('resize', showButton);
  return (
   <>
        <nav className='navbar'> 
           <div className='navbar-container'>
                <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
                    AWD <i className='fab fa-typo3' />
                </Link>  
                {/* Ovde smo ubacili da ako je kliknuto onda ce biti X a ako nije onda ce biti linice 
                    Onda moramo da definisemo click i da ga postavimo setujemo (setClick) i tu koristimo i useState
                    useState koristimo kako bi updatovali stanje "dugmeta" iz X u linice, mi smo za sada samo definisali click ali
                    nemamo funkciju koja ce da nam menja stanje dugmeta a to je (hendleClick) */}
                <div className='menu-icon' onClick={handleClick}>
                    <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                </div>
                <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                    <li className='nav-item'>
                        <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                            Home
                        </Link>
                    </li>
                    <li className='nav-item'>
                        <Link to='/services' className='nav-links' onClick={closeMobileMenu}>
                            Services
                        </Link>
                    </li>
                    <li className='nav-item'>
                        <Link to='/products' className='nav-links' onClick={closeMobileMenu}>
                            Products
                        </Link>
                    </li>
                    <li className='nav-item'>
                        <Link to='/sign-up' className='nav-links-mobile' onClick={closeMobileMenu}>
                            Sign up
                        </Link>
                    </li>
                </ul>
                {button && <Button buttonStyle='btn-outline'>SING UP</Button>}
           </div>
        </nav>
   </>
  );
}

export default Navbar;
